
$(function testLink() {
    var yOff = 15;
    var xOff = 20;
    var scndxOff = 260;
    $(".text-hover-image").hover(function (e) {
        if ($(this).data('secondFace')){
            var imgSrc = $(this).data('img-for-hover')
            var secondSrc = $(this).data('second-face')
            $("body").append(`<p id='image-when-hovering-text'><img src= ${imgSrc}/></p>`, `<p id='second-image-display'><img src= ${secondSrc}></p>`);
            $("#image-when-hovering-text")
                .css("position", "absolute")
                .css("top", (e.pageY - yOff) + "px")
                .css("left", (e.pageX + xOff) + "px")
                .css({ 'height': '10px', 'width': '10px' })
                .fadeIn("fast");
            $('#second-image-display')
                .css("position", "absolute")
                .css("top", (e.pageY - yOff) + "px")
                .css("left", (e.pageX + scndxOff) + "px")
                .css({ 'height': '10px', 'width': '10px' })
                .fadeIn("fast");
        } else {
            var imgSrc = $(this).data('img-for-hover')
            $("body").append(`<p id='image-when-hovering-text'><img src= ${imgSrc}/></p>`);
            $("#image-when-hovering-text")
                .css("position", "absolute")
                .css("top", (e.pageY - yOff) + "px")
                .css("left", (e.pageX + xOff) + "px")
                .css({ 'height': '10px', 'width': '10px' })
                .fadeIn("fast");
        }
    },
    
    function () {
        $("#image-when-hovering-text").remove()
        $('#second-image-display').remove();
    });
    
    $(".text-hover-image").mousemove(function (e) {
        $("#image-when-hovering-text")
            .css("top", (e.pageY - yOff) + "px")
            .css("left", (e.pageX + xOff) + "px")
        $("#second-image-display")
            .css("top", (e.pageY - yOff) + "px")
            .css("left", (e.pageX + scndxOff) + "px");
    });

    hoverImage = testLink

})

