

$(function testLink() {
    var yOff = 15;
    var xOff = 20;
    $(".text-hover-image").hover(function (e) {
        var imgSrc = $(this).data('img-for-hover')
        $("body").append(`<p id='image-when-hovering-text'><img src= ${imgSrc}/></p>`);
        $("#image-when-hovering-text")
            .css("position", "absolute")
            .css("top", (e.pageY - yOff) + "px")
            .css("left", (e.pageX + xOff) + "px")
            .css({ 'height': '10px', 'width': '10px' })
            .fadeIn("fast");
    },
    
    function () {
        $("#image-when-hovering-text").remove();
    });
    
    $(".text-hover-image").mousemove(function (e) {
        $("#image-when-hovering-text")
            .css("top", (e.pageY - yOff) + "px")
            .css("left", (e.pageX + xOff) + "px");
    });
    hoverImage = testLink

})

